import { createStore } from 'vuex'
import axios from 'axios'

const store = createStore({
    state: {
      users: [],
      totalRecords: null,
      totalPages: null,
      pageSize: null,
      pageNumber: null,
      recordStart: null,
      recordEnd: null,
    },
    mutations: {
      getUserPaging(state, response){
        state.users = response;
      },
    },
    actions: {
      async getUserData({commit}){
        try {
          await axios.get("https://localhost:44382/api/v1/Users").then((response) => {
            commit('getUser', response)
          })
        } catch (error) {
          console.log(error);
        }
      }
    }
})

export default store
