import axios from 'axios'
const state = {
  users: [],
  userPaging: {
    totalRecords: null,
    totalPages: null,
    pageSize: null,
    pageNumber: null,
    recordStart: null,
    recordEnd: null,
  },
}

const getters = {
    users(state){
        return state.users;
    },
    userPaging(state){
        return state.userPaging;
    }
}
const mutations = {
  getUserPaging(state, response) {
    state.users = response.Data;
    state.totalRecords = response.TotalRecords;
    state.totalPages = response.TotalPages;
    state.pageSize = response.PageSize;
    state.pageNumber = response.PageNumber;
    state.recordStart = response.RecordStart;
    state.recordEnd = response.RecordEnd;
  },
}

const actions = {
  async getUserData({ commit }, { pageSize, pageNumber, searchKey }) {
    try {
      if (searchKey == "") {
        await axios
          .get(
            `https://localhost:44382/api/v1/Users` +
              "/Filter?pageSize=" +
              pageSize +
              "&pageNumber=" +
              pageNumber
          )
          .then((response) => {
            commit("getUserPaging", response.data);
          });
      } else {
        await axios
          .get(
            `https://localhost:44382/api/v1/Users` +
              "/Filter?pageSize=" +
              pageSize +
              "&pageNumber=" +
              pageNumber +
              "&searchKey=" +
              searchKey
          )
          .then((response) => {
            commit("getUserPaging", response.data);
          });
      }
    } catch (error) {
      console.log(error);
    }
  },
}

export default {
    state,
    getters,
    mutations,
    actions
}
