    try {
      await axios
        .get("https://localhost:44382/api/v1/UserGroups")
        .then((response) => {
          commit("getAllGroup", response.data);
        });
    } catch (error) {
      console.log(error);
    }
  },
};

export default {
  state,
  getters,
  mutations,
  actions,
};
