import axios from 'axios'
const state = {
  usergroups: [],
  userGroupPaging: {
    totalRecords: null,
    totalPages: null,
    pageSize: null,
    pageNumber: null,
    recordStart: null,
    recordEnd: null,
  },
}

const getters = {
    usergroups(state){
        return state.usergroups;
    },
    userGroupPaging(state){
        return state.userGroupPaging;
    }
}
const mutations = {
  getUserpGrouPaging(state, response) {
    state.userGroups = response.Data;
    state.userGroupPaging.totalRecords = response.TotalRecords;
    state.userGroupPaging.totalPages = response.TotalPages;
    state.userGroupPaging.pageSize = response.PageSize;
    state.userGroupPaging.pageNumber = response.PageNumber;
    state.userGroupPaging.recordStart = response.RecordStart;
    state.userGroupPaging.recordEnd = response.RecordEnd;
  },
}

const actions = {
  async getUserGroupData({ commit }, { pageSize, pageNumber, searchKey }) {
    try {
      if (searchKey == "") {
        await axios
          .get(
            `https://localhost:44382/api/v1/UserGroups` +
              "/Filter?pageSize=" +
              pageSize +
              "&pageNumber=" +
              pageNumber
          )
          .then((response) => {
            commit("getUserGroupPaging", response.data);
          });
      } else {
        await axios
          .get(
            `https://localhost:44382/api/v1/UserGroups` +
              "/Filter?pageSize=" +
              pageSize +
              "&pageNumber=" +
              pageNumber +
              "&searchKey=" +
              searchKey
          )
          .then((response) => {
            commit("getUserGroupPaging", response.data);
          });
      }
    } catch (error) {
      console.log(error);
    }
  },
}

export default {
    state,
    getters,
    mutations,
    actions
}
