import { createStore } from 'vuex'
import axios from 'axios'
import user from './modules/user.js'

const store = createStore({
  modules: {
    user
  },
    state: {
      users: [],
      totalRecords: null,
      totalPages: null,
      pageSize: null,
      pageNumber: null,
      recordStart: null,
      recordEnd: null,
    },
    mutations: {
      getUserPaging(state, response){
        state.users = response.Data;
        state.totalRecords = response.TotalRecords;
        state.totalPages = response.TotalPages;
        state.pageSize = response.PageSize;
        state.pageNumber = response.PageNumber;
        state.recordStart = response.RecordStart;
        state.recordEnd = response.RecordEnd;
      },
    },
    actions: {
      async getUserData({commit}, {pageSize, pageNumber, searchKey}){
        try {
          if(searchKey == ""){
            await axios.get(`https://localhost:44382/api/v1/Users`+"/Filter?pageSize=" + pageSize + "&pageNumber=" + pageNumber).then((response) => {
              commit('getUserPaging', response.data)
            })
          } else {
            await axios.get(`https://localhost:44382/api/v1/Users`+"/Filter?pageSize=" + pageSize + "&pageNumber=" + pageNumber +"&searchKey="+searchKey).then((response) => {
              commit('getUserPaging', response.data)
            })
          }
        } catch (error) {
          console.log(error);
        }
      }
    }
})

export default store
